// Earth radius in meters
const R = 6371000;

// Convert degrees to radians
const toRad = deg => deg * Math.PI / 180;

// Extract coordinates from the input JSON safely
const inputJson = $input.first().json;
let lat, lon;
if (inputJson.messages && Array.isArray(inputJson.messages) && inputJson.messages[0] && inputJson.messages[0].location) {
  lat = inputJson.messages[0].location.latitude;
  lon = inputJson.messages[0].location.longitude;
} else {
  // Handle missing data as needed; here, we return an error object
  return [{
    json: {
      error: "Missing location data in input"
    }
  }];
}

// Office polygon vertices [longitude, latitude]
const officePolygon = [

];

// Set distance threshold (increase for more leeway)
const distanceThreshold = 2500;

let isNearby = false;

// Haversine distance function
function haversine(lat1, lon1, lat2, lon2) {
  const dLat = toRad(lat2 - lat1);
  const dLon = toRad(lon2 - lon1);
  const a = Math.sin(dLat / 2) ** 2 +
            Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) *
            Math.sin(dLon / 2) ** 2;
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
}

// Check if user is within threshold distance of any polygon point
for (const [polyLon, polyLat] of officePolygon) {
  const distance = haversine(lat, lon, polyLat, polyLon);
  if (distance <= distanceThreshold) {
    isNearby = true;
    break;
  }
}

// Output result with user's location and isInsideOffice flag
return [{
  json: {
    ...$input.first().json,
    latitude: lat,
    longitude: lon,
    isInsideOffice: isNearby
  }
}];

